    private void btnCalcularMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnCalcularMouseClicked

        double horas = Double.parseDouble(txtHora.getText());
        double minutos = Double.parseDouble(txtMinuto.getText());
        double total;

        if (minutos >= 60) {
            double temp = (minutos / 60);
            horas += temp;
            minutos = 0;
        }

        minutos = (minutos / 60);
        horas += minutos;

        if (horas <= 1) {
            total = 2;
        } else if ((horas > 1) && (horas <= 2)) {
            total = 3.5;
        } else {
            horas -= 2;
            total = 3.5 + horas;
        }

        String somaTotal = Double.toString(total);

        txtTotal.setText("R$ " + somaTotal);
    }//GEN-LAST:event_btnCalcularMouseClicked

